import React, { Component } from 'react';
import { variables } from './Variables.js';
import {BrowserRouter as Router, Route, Routes, NavLink} from 'react-router-dom';

export class Machine extends Component {

    constructor(props) {
        super(props);

        this.state = {
            machines: [],
            modalTitle: "",
            MachineId: 0,
            MachineName: "",
            MachineClass: "",
            MachineCompany: "",
            MachineState: "",

            MachineIdFilter: "",
            MachineNameFilter: "",
            MachineClassFilter: "",
            MachineStateFilter: "",
            MachinesWithoutFilter: []
        }
    }

    FilterFunction() {
        var MachineIdFilter = this.state.MachineIdFilter;
        var MachineNameFilter = this.state.MachineNameFilter;
        var filteredData = this.state.MachinesWithoutFilter.filter(
            function (el) {
                return el.id.toString().toLowerCase().includes(
                    MachineIdFilter.toString().trim().toLowerCase()
                ) &&
                    el.description.toString().toLowerCase().includes(
                        MachineNameFilter.toString().trim().toLowerCase()
                    )
            }
        );

        this.setState({ machines: filteredData });

    }


    changeMachineIdFilter = (e) => {
        this.state.MachineIdFilter = e.target.value;
        this.FilterFunction();
    }
    changeMachineNameFilter = (e) => {
        this.state.MachineNameFilter = e.target.value;
        this.FilterFunction();
    }


    refreshList() {
        fetch(variables.API_URL)
            .then(response => response.json())
            .then(data => {
                this.setState({ machines: data, MachinesWithoutFilter: data });
            });
    }

    componentDidMount() {
        this.refreshList();
    }

    render() {
        const {
            machines,
            modalTitle,
            MachineId,
            MachineName,
            MachineClass,
            MachineCompany,
            MachineState,
        } = this.state;

        return (
            <div>
                <header class="cabecera">
                    <div id="logo">
                        <div id="logoUnimap">
                            <img src="./Assets/logo35x35.png" alt="logo de unimap" />
                        </div>
                        <h1 class="logoh1">
                            Uni<strong>map</strong>
                        </h1>
                    </div>
                    <div id="search">

                        <input type="search" placeholder="buscar" onChange={this.changeMachineNameFilter} />


                    </div>


                </header>
                <div class="caja1">
                <div class="number">
                <input type="search" placeholder="buscar" onChange={this.changeMachineIdFilter} />
                </div>
                {machines.map(machin =>
                <NavLink to="/home" Component={machin.id}>
                    <a href='#exampleModal' data-bs-toggle="modal" data-bs-target="#exampleModal" onClick={() => this.editClick(machin)}>
                        <div class="renglon" key={machin.id}>
                            <div class="number">
                                    ({machin.id})
                                </div>
                                    <p class="text">
                                    {machin.description}
                                    </p>
                                <div className="boton">
                                {machin.working ? <button className="button button1"></button> : <button class="button button2"></button>}
                            </div> 
                        </div>
                    </a>
                </NavLink>
                )}
                </div>

                <div className="modal fade" id="exampleModal" tabIndex="-1" aria-hidden="true">
                    <div className="modal-dialog modal-lg modal-dialog-centered">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{modalTitle}</h5>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="close"></button>
                            </div>
                            <div className="modal-body">
                                <div className="input-group mb-3">
                                    <span className="input-group-text">MachineName</span>
                                    <input type="text" className="form-control" value={MachineName} onChange={this.changeMachineName} />
                                </div>
                                <div className="input-group mb-3">
                                    <span className="input-group-text">MachineClass</span>
                                    <input type="text" className="form-control" value={MachineClass} onChange={this.changeMachineClass} />
                                </div>
                                <div className="input-group mb-3">
                                    <span className="input-group-text">MachineCompany</span>
                                    <input type="text" className="form-control" value={MachineCompany} onChange={this.changeMachineCompany} />
                                </div>
                                <div className="input-group mb-3">
                                    <span className="input-group-text">Estado</span>
                                    <input type="text" className="form-control" value={MachineState} onChange={this.changeMachineState} />
                                </div>


                                {MachineId == 0 ?
                                    <button type="button"
                                        className="btn btn-primary float-start"
                                        onClick={() => this.createClick()}
                                    >Create</button>
                                    : null}

                                {MachineId != 0 ?
                                    <button type="button"
                                        className="btn btn-primary float-start"
                                        onClick={() => this.updateClick()}
                                    >Update</button>
                                    : null}

                            </div>
                        </div>
                    </div>
                </div>

            </div>

                    

        )
    }
}
